import 'dart:convert';import '../../model/response/login_reponse.dart';import '../../model/response/user_response.dart';import '../api.dart';import '../endpoints.dart';import 'package:http/http.dart' as http;class UserRepository {  API api = API();  Future<LoginResponse> login(data) async {    print("DATA :: " + data.toString());    dynamic response = await api.postData(data, Endpoints.login);    print("USER RESPONSE :: " + response.toString());    LoginResponse res = LoginResponse.fromJson(response);    return res;  }  Future<UserResponse> getUser() async {    dynamic response;    UserResponse res;    try {      response = await api.getWithToken(Endpoints.user);      print("RESSS::${response}");      res = UserResponse.fromJson(response);    } catch (e) {      res = UserResponse.fromJson(response);    }    return res;  }}