import 'package:flutter/material.dart';import '../constants/app_color.dart';import '../constants/app_image.dart';import '../constants/font_size.dart';import '../constants/font_weight.dart';class NoInternetWidget extends StatefulWidget {  NoInternetWidget({    Key? key,    this.title = "Something went wrong",    this.content = "We're having issues loading this page",    this.buttonText = "Try again",    this.isShowBorder = true,    this.onTab,  }) : super(key: key);  String? title;  String? content;  String? buttonText;  bool? isShowBorder;  Function()? onTab;  @override  State<NoInternetWidget> createState() => _NoInternetWidgetState();}class _NoInternetWidgetState extends State<NoInternetWidget> {  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.symmetric(vertical: 8),      width: MediaQuery.of(context).size.width,      // height: double.infinity,      height: MediaQuery.of(context).size.height /1.5,      child: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          widget.isShowBorder == false ? const Icon(            Icons.wifi_off,            size: 30,          ) :          Image.asset(kNoConnection),          const SizedBox(            height: 10,          ),          Text(            widget.isShowBorder == true ? 'Something went wrong' : 'No Internet Connection',            style: TextStyle(fontSize: font_20, fontWeight: kW6),          ),          const SizedBox(            height: 10,          ),          Text(            widget.isShowBorder == true ? "We're having issues loading this page." : 'Please connect to the internet and try again',            style: const TextStyle(fontSize: font_16),          ),          const SizedBox(            height: 20,          ),          widget.isShowBorder == false ?          Align(              alignment: Alignment.bottomCenter,              child: FlatButton.icon(                  onPressed: widget.onTab ,                  icon: Icon(Icons.refresh),                  label: Text("Refresh"))) :          InkWell(            onTap: (){              // Navigator.popAndPushNamed(context, '/');            },            child: Container(              padding: const EdgeInsets.symmetric(                  horizontal: 10, vertical: 5),              decoration: BoxDecoration(                borderRadius: BorderRadius.circular(20),                // color: whi,                border: Border.all(color: tabBlue),              ),              child: const Text(                "Try again",                style: TextStyle(                    color: tabBlue,                    fontWeight: kW6,                    fontSize: font_16),              ),            ),          )        ],      ),    );  }}