import 'package:flutter/material.dart';import 'package:qr_flutter/qr_flutter.dart';import '../../widget/custom_app_bar.dart';class GenerateQRScreen extends StatefulWidget {  dynamic args;  GenerateQRScreen({Key? key, required this.args}) : super(key: key);  @override  State<StatefulWidget> createState() => GenerateQRScreenState();}class GenerateQRScreenState extends State<GenerateQRScreen> {  @override  void initState() {    changeQR();    super.initState();  }  changeQR(){    print("QR ID:::::${widget.args.toString()}");    setState(() {      dummyData = widget.args.toString();    });  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: appBar(        noBackButton: true,        navTitle: "QR Code",      ),      body: ListView(        padding: const EdgeInsets.symmetric(vertical: 20, horizontal: 10),        children: <Widget>[          const SizedBox(height: 20,),          (dummyData == null)              ? const Center(child: Text("Something went wrong. Please try again later."))              : QrImage(            // embeddedImage: const NetworkImage(            //   "https://avatars1.githubusercontent.com/u/41328571?s=280&v=4",            // ),            data: dummyData!,            gapless: true,          ),        ],      ),    );  }}String? dummyData;