import 'package:flutter/material.dart';import 'package:responsive_grid/responsive_grid.dart';import '../../../constants/app_color.dart';import '../../widget/custom_app_bar.dart';class TechnicianDashboard extends StatefulWidget {  const TechnicianDashboard({Key? key}) : super(key: key);  @override  State<TechnicianDashboard> createState() =>      _TechnicianDashboardState();}class _TechnicianDashboardState extends State<TechnicianDashboard> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: appBar(        noBackButton: false,        navTitle: "Dashboard",      ),      body: SafeArea(          child: Padding(        padding:            const EdgeInsets.symmetric(horizontal: 20, vertical: 10),        child: ResponsiveGridRow(          rowSegments: 1,            children: [          ResponsiveGridCol(            xs: 6,            md: 3,            child: dashBoardCardGrid(" Total Active Issues Submitted "),          ),          ResponsiveGridCol(            xs: 6,            md: 3,            child: dashBoardCardGrid("Total Active Issues Solved "),          ),          ResponsiveGridCol(            xs: 6,            md: 3,            child: dashBoardCardGrid("Total Issues Solved" ),          ),          ResponsiveGridCol(            xs: 6,            md: 3,            child: dashBoardCardGrid("Total Issues Solved" ),          ),        ]),      )),    );  }  Widget dashBoardCardGrid(String name) {    return GestureDetector(      child: Card(          margin: const EdgeInsets.symmetric(horizontal: 5, vertical: 10),          color: black,          shape: RoundedRectangleBorder(              borderRadius: BorderRadius.circular(15)),          elevation: 5,          child: Padding(            padding: const EdgeInsets.symmetric(horizontal: 10, vertical: 20),            child: Column(              children: [                const Text(                  "1",                  style: TextStyle(                      fontSize: 30,                      color: white,                      fontWeight: FontWeight.w900),                ),                const SizedBox(height: 5),                Text(                  name,                  style: const TextStyle(                      fontSize: 15,                      color: white,                      fontWeight: FontWeight.w900),                )              ],            ),          )),    );  }}